import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { CouriersService } from './couriers.service';
import { Couriers } from './Couriers';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {


  courier = new Couriers();

  price: number | undefined;
  // Dependency Injection
  constructor(http: HttpClient, private couriersService: CouriersService) {}

  ngOnInit(): void {
   
  }

  checkPrice() {
    this.couriersService.checkPrice(this.courier)
      .subscribe(data => {
        console.log(data)
        this.displayPrice(data);
      })
  }

  displayPrice(_data: number | undefined) {
    this.price = _data;
  }

  title = 'PageUp Courier System';



}

<h1>{{title}}</h1>

<div style="margin-left:100px;">
  <div>
    <label>Weight: </label>
    <input [(ngModel)]="courier.weight" />
  </div>

  <br />

  <div>
    <label>Height: </label>
    <input [(ngModel)]="courier.height" />
  </div>
  <br />
  <div>
    <label>Width: </label>
    <input [(ngModel)]="courier.width" />
  </div>
  <br />
  <div>
    <label>Depth: </label>
    <input [(ngModel)]="courier.depth" />
  </div>
  <br />
  <div>
    <br />
    <button (click)="checkPrice()">Check Price</button>
  </div>
</div>
