import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { CouriersService } from './couriers.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  public forecasts?: WeatherForecast[];

  //couriers = {
  //  weight: number = 0,
  //  height: number,
  //  width: number,
  //  depth: number,
  //  published: false
  //};

  weight: number = 0;
  height: number = 0;
  width: number = 0;
  depth: number = 0;
  published = false;
  submitted = false;



  constructor(http: HttpClient, private couriersService: CouriersService) {
    http.get<WeatherForecast[]>('/weatherforecast').subscribe(result => {
      this.forecasts = result;
    }, error => console.error(error));
  }
    ngOnInit(): void {
        throw new Error('Method not implemented.');
    }

  getPrice(): void {
    const data = {
      //weight: this.couriers.weight,
      //height: this.couriers.height,
      //width: this.couriers.width,
      //depth: this.couriers.depth,

      weight: this.weight,
      height: this.height,
      width: this.width,
      depth: this.depth,
    };

    this.couriersService.create(data)
      .subscribe(
          (        response: any) => {
          console.log(response);
          this.submitted = true;
        },
          (        error: any) => {
          console.log(error);
        });
  }

  //newCourier(): void {
  //  this.submitted = false;
  //  this.couriers = {
  //    weight: 0,
  //    height: 0,
  //    width: 0,
  //    depth: 0,
  //    published: false
  //  };
  //}


  title = 'angularapp';



}

interface WeatherForecast {
  date: string;
  temperatureC: number;
  temperatureF: number;
  summary: string;
}

interface OrderInputDetails {
  weight: number;
  height: number;
  width: number;
  depth: number;
}
